import { extendTheme } from '@chakra-ui/react'

export const theme = extendTheme({
  colors: {
    black: {
      "700": "#0F0F0F",
      "500": "#191919",
    },
    purple: {
      "900": "linear-gradient(144deg, rgba(25,14,34,1) 0%, rgba(44,14,59,1) 36%, rgba(72,11,128,1) 84%, rgba(89,3,168,1) 100%)",
    },
    white: {
      "500": "FFFFFFCC",
    }
  },
  fonts: {
    heading: 'Roboto',
    body: 'Roboto'
  },
  styles: {
    global: {
      body: {
        bg: 'purple.900',
        color: 'white.500'
      }
    }
  }
})
