import styled from 'styled-components';


export const MapDiv = styled.div`
margin-top: 2rem;
display: flex;
align-items: center;
justify-content: center;
flex-direction: column;
`;

export const MapDivTop10 = styled.div`
display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 1rem;
    margin-bottom: 2rem;
`;
